t.script:
document.addEventListener('DOMContentLoaded', function() {
    const acceptBtn = document.getElementById('btn-accept');
    const declineBtn = document.getElementById('btn-decline');

    if (acceptBtn) {
        acceptBtn.addEventListener('click', async function() {
            if (!confirm('Are you sure you want to ACCEPT this work order?')) return;

            this.disabled = true;
            this.innerHTML = 'Processing...';

            const id = this.dataset.id;
            const token = this.dataset.token;

            try {
                const response = await fetch(`/my/work-orders/${id}/accept`, {
                    method: 'POST',
                    headers: {'Content-Type': 'application/json'},
                    body: JSON.stringify({
                        jsonrpc: '2.0',
                        method: 'call',
                        params: {access_token: token}
                    })
                });

                const result = await response.json();
                if (result.result && result.result.success) {
                    location.reload();
                } else {
                    alert('Error: ' + (result.result?.error || 'Unknown error'));
                    this.disabled = false;
                    this.innerHTML = 'Accept This Assignment';
                }
            } catch (e) {
                alert('Network error. Please try again.');
                this.disabled = false;
                this.innerHTML = 'Accept This Assignment';
            }
        });
    }

    if (declineBtn) {
        declineBtn.addEventListener('click', async function() {
            const reason = prompt('Please provide a reason for declining (optional):');
            if (reason === null) return; // User cancelled

            this.disabled = true;
            this.innerHTML = 'Processing...';

            const id = this.dataset.id;
            const token = this.dataset.token;

            try {
                const response = await fetch(`/my/work-orders/${id}/decline`, {
                    method: 'POST',
                    headers: {'Content-Type': 'application/json'},
                    body: JSON.stringify({
                        jsonrpc: '2.0',
                        method: 'call',
                        params: {access_token: token, reason: reason}
                    })
                });

                const result = await response.json();
                if (result.result && result.result.success) {
                    location.reload();
                } else {
                    alert('Error: ' + (result.result?.error || 'Unknown error'));
                    this.disabled = false;
                    this.innerHTML = 'Decline';
                }
            } catch (e) {
                alert('Network error. Please try again.');
                this.disabled = false;
                this.innerHTML = 'Decline';
            }
        });
    }
});
